INSERT INTO sectors (name, description) VALUES ( 'Financial Services', 'Encompasses banks, insurance companies, investment firms, and financial advisory services. Plays a critical role in managing money, facilitating investments, and providing financial solutions for individuals and businesses.');
INSERT INTO sectors (name, description) VALUES ( 'Diversified', 'Companies with multiple business lines across different industries, reducing risk through varied revenue streams. These organizations operate in multiple sectors, leveraging synergies and cross-industry opportunities.');
INSERT INTO sectors (name, description) VALUES ( 'Capital Goods', 'Includes machinery, equipment, and tools used in manufacturing and industrial production. Represents the backbone of industrial infrastructure and technological advancement.');
INSERT INTO sectors (name, description) VALUES ( 'Construction Materials', 'Producers of cement, aggregates, steel, wood, and other materials essential for building and infrastructure development. Critical for construction, urban development, and infrastructure projects.');
INSERT INTO sectors (name, description) VALUES ( 'Chemicals', 'Manufacturers of industrial chemicals, specialty chemicals, and petrochemicals. Provides essential materials for manufacturing, agriculture, pharmaceuticals, and numerous other industries.');
INSERT INTO sectors (name, description) VALUES ( 'Healthcare', 'Includes hospitals, pharmaceutical companies, medical device manufacturers, and healthcare services. Focuses on medical treatment, research, drug development, and patient care.');
INSERT INTO sectors (name, description) VALUES ('Oil Gas & Consumable Fuels', 'Exploration, production, refining, and distribution of petroleum and natural gas products. Crucial for global energy supply and transportation.');
